import { Component, OnInit, OnDestroy } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { MessageService, MessageRequest, SendMessageResponse } from './message.service';
import { WebSocketService, MessageResponse } from './websocket.service';

interface DisplayMessage {
  id: string;
  sender: string;
  content: string;
  status: 'sending' | 'sent' | 'replied';
  timestamp: Date;
  response?: string;
  responseTimestamp?: Date;
}

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit, OnDestroy {
  messageContent: string = '';
  senderName: string = '';
  messages: DisplayMessage[] = [];
  isConnected: boolean = false;

  constructor(
      private messageService: MessageService,
      private webSocketService: WebSocketService
  ) {}

  ngOnInit(): void {
    // Connect to WebSocket
    this.webSocketService.connect();

    // Check connection status
    setTimeout(() => {
      this.isConnected = this.webSocketService.isConnected();
    }, 1000);

    // Subscribe to WebSocket messages
    this.webSocketService.messages$.subscribe((response: MessageResponse) => {
      this.handleWebSocketMessage(response);
    });
  }

  ngOnDestroy(): void {
    this.webSocketService.disconnect();
  }

  sendMessage(): void {
    if (!this.messageContent.trim() || !this.senderName.trim()) {
      alert('Please fill in both sender name and message content');
      return;
    }

    const request: MessageRequest = {
      content: this.messageContent,
      sender: this.senderName
    };

    this.messageService.sendMessage(request).subscribe({
      next: (response: SendMessageResponse) => {
        // Add message to display with 'sent' status
        const displayMessage: DisplayMessage = {
          id: response.correlationId,
          sender: this.senderName,
          content: this.messageContent,
          status: 'sent',
          timestamp: new Date()
        };

        this.messages.unshift(displayMessage);

        // Clear form
        this.messageContent = '';

        console.log('Message sent successfully:', response);
      },
      error: (error) => {
        console.error('Error sending message:', error);
        alert('Error sending message. Please try again.');
      }
    });
  }

  private handleWebSocketMessage(response: MessageResponse): void {
    console.log('üîî WebSocket received:', response);

    const messageIndex = this.messages.findIndex(m => m.id === response.correlationId);

    if (messageIndex !== -1) {
      this.messages[messageIndex] = {
        ...this.messages[messageIndex],
        status: 'replied',
        responseTimestamp: new Date(response.timestamp)
      };

      this.messages = [...this.messages];

      console.log('‚úÖ Message updated at index:', messageIndex);
    } else {
      const displayMessage: DisplayMessage = {
        id: response.correlationId,
        sender: 'Unknown',
        content: 'Original message not found',
        status: 'replied',
        timestamp: new Date(response.timestamp),
        response: response.data.content,
        responseTimestamp: new Date(response.timestamp)
      };

      this.messages.unshift(displayMessage);
      console.log('‚ö†Ô∏è Original message not found, created new entry');
    }
  }

  getStatusClass(status: string): string {
    switch (status) {
      case 'sending':
        return 'status-sending';
      case 'sent':
        return 'status-sent';
      case 'replied':
        return 'status-replied';
      default:
        return '';
    }
  }

  getStatusText(status: string): string {
    switch (status) {
      case 'sending':
        return 'Sending...';
      case 'sent':
        return 'Waiting for reply...';
      case 'replied':
        return 'Replied';
      default:
        return '';
    }
  }
}
<div class="container">
  <div class="header">
    <h1>ActiveMQ Camel Messaging</h1>
    <div class="connection-status" [class.connected]="isConnected" [class.disconnected]="!isConnected">
      <span class="status-dot"></span>
      {{ isConnected ? 'Connected' : 'Disconnected' }}
    </div>
  </div>

  <div class="message-form">
    <h2>Send Message</h2>
    <form (ngSubmit)="sendMessage()">
      <div class="form-group">
        <label for="sender">Your Name:</label>
        <input
          type="text"
          id="sender"
          [(ngModel)]="senderName"
          name="sender"
          placeholder="Enter your name"
          required
        />
      </div>

      <div class="form-group">
        <label for="message">Message:</label>
        <textarea
          id="message"
          [(ngModel)]="messageContent"
          name="message"
          placeholder="Enter your message"
          rows="4"
          required
        ></textarea>
      </div>

      <button type="submit" class="btn-send">
        Send Message
      </button>
    </form>
  </div>

  <div class="messages-container">
    <h2>Messages</h2>

    <div *ngIf="messages.length === 0" class="no-messages">
      No messages yet. Send your first message!
    </div>

    <div *ngFor="let message of messages" class="message-card">
      <div class="message-header">
        <div class="message-info">
          <span class="sender">{{ message.sender }}</span>
          <span class="timestamp">{{ message.timestamp | date:'short' }}</span>
        </div>
        <div class="status" [ngClass]="getStatusClass(message.status)">
          {{ getStatusText(message.status) }}
        </div>
      </div>

      <div class="message-body">
        <div class="message-content">
          <strong>Message:</strong>
          <p>{{ message.content }}</p>
        </div>

        <div *ngIf="message.response" class="response-content">
          <strong>Response:</strong>
          <p>{{ message.response }}</p>
          <span class="response-time" *ngIf="message.responseTimestamp">
            Received at: {{ message.responseTimestamp | date:'short' }}
          </span>
        </div>
      </div>

      <div class="correlation-id">
        <small>Correlation ID: {{ message.id }}</small>
      </div>
    </div>
  </div>
</div>
